import { createUseStyles } from 'react-jss';

export const useStyles = createUseStyles({
  wrapper: {
    maxWidth: '600px',
    margin: '0 auto',
    marginTop: '30px',
    display: 'flex',
    flexDirection: 'column',
    boxShadow: '0 12px 12px -6px #026570'
  },
  input: {
    margin: '0 4px 0 10px'
  },
  label: {
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'raw'
  },
  giveWrapper: {
    width: '100%',
    height: '240px',
    backgroundColor: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  getWrapper: {
    width: '100%',
    height: '240px',
    backgroundColor: '#b0d8dd',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  }
});
