import { createUseStyles } from 'react-jss';
import { relativeValue } from '../styleUtils';
import { COLOR } from '../styleVariables';

const boxWrapper = {
  marginLeft: relativeValue(30),
  marginRight: relativeValue(30)
};

export const useStyles = createUseStyles({
  header: {
    width: '100vw',
    height: relativeValue(80),
    backgroundColor: COLOR.ONAHAU,
    borderRadius: '10px',
    display: 'flex',
    alignItems: 'center'
  },
  logoWrapper: {
    extend: boxWrapper,
    height: relativeValue(40),
    width: relativeValue(200),
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: relativeValue(6),
    borderStyle: 'solid',
    borderImage: 'linear-gradient(to left, turquoise, greenyellow) 1 0',
  },
  ratesWrapper: {
    extend: boxWrapper
  },
  rateWrapper: {
    width: relativeValue(180),
    height: relativeValue(20),
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    fontWeight: 'bold'
  }
})
