import React, { useEffect, useState } from 'react';
import { Header } from "./components/Header/Header";
import './App.css';
import { CurrencyConverter } from './components/CurrencyConverter/CurrencyConverter';

const BASE_URL = 'https://currency-converter5.p.rapidapi.com/currency/list';

export interface ICurrency {
  cc: string;
  rate: number;
  txt: string;
}

function App() {
  const [currencies, setCurrencies] = useState<ICurrency[]>([]);
  const currencyOptions = ['USD', 'EUR', 'UAH'];

  useEffect(() => {
    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': '627db79484msh688fc86f830cc6ap17443bjsn353771dfb992',
        'X-RapidAPI-Host': 'currency-converter5.p.rapidapi.com'
      }
    };

    fetch('https://currency-converter5.p.rapidapi.com/currency/list', options)
      .then(response => {
        console.log('1 - ', response.json())
      })
      .then(response => console.log('2 - ', response))
      .catch(err => console.error(err));
  }, []);

  return (
    <div>
      {/*<Header currencies={currencies.filter(c => currencyOptions.includes(c.cc))} />*/}
      <h1 className="title">Currency Converter</h1>
      {currencies &&
        <CurrencyConverter currencies={currencies} />
      }
    </div>
  );
}

export default App;
