import React from 'react';
import { useStyles } from './CurrencyConverter.styles';

interface Props {
  currencies: { [key: string]: number };
}

export const CurrencyConverter:React.FC<Props> = ({ currencies }) => {
  const styles = useStyles();

  console.log(currencies['EUR']);
  return (
    <div className={styles.wrapper}>
      <div className={styles.giveWrapper}>
        <label className={styles.label}>
          <p>You give</p>
          <input
            className={styles.input}
            type="number"
          />
        </label>
        <select
          name="currency"
        >
          {Object.keys(currencies).map(currency => (
            <option key={currency} value={currency}>{currency}</option>
          ))}
          <option value="UAH">UAH</option>
        </select>
      </div>
      <div className={styles.getWrapper}>
        <label className={styles.label}>
          <p>You get</p>
          <input className={styles.input} type="number" readOnly />
        </label>
        <select
          name="currency"
        >
          {/*{currencies.map(currency => (*/}
          {/*  <option key={currency.cc} value={currency.rate}>{currency.cc}</option>*/}
          {/*))}*/}
          <option value="UAH">UAH</option>
        </select>
      </div>
    </div>
  );
};
