const pre = 'https://api.exchangerate.host';

const get = (url: string) =>
  fetch(`${pre}${url}`, {
    method: 'GET'
  }).then(res => {
    if (res.ok) {
      return res.json();
    } else {
      throw new Error('Something went wrong');
    }
  });

const api = { get };

export default api;
