import React, { useEffect, useState } from 'react';
import { Header } from "./components/Header/Header";
import './App.css';
import { CurrencyConverter } from './components/CurrencyConverter/CurrencyConverter';
import { getAllCurrencies } from './services/currencies';

export interface ICurrency {
  cc: string;
  rate: number;
  txt: string;
}

function App() {
  const [allCurrencies, setAllCurrencies] = useState();
  const currencyOptions = ['USD', 'EUR', 'UAH'];
  const aa = getAllCurrencies();

  useEffect(() => {
    async function getCurrencies() {
      await getAllCurrencies()
        .then(res => setAllCurrencies(res.json()));
    }

    getCurrencies();
  }, []);

  console.log(allCurrencies);
  return (
    <div>
      {/*<Header currencies={currencies.filter(c => currencyOptions.includes(c.cc))} />*/}
      <h1 className="title">Currency Converter</h1>
      {currencies &&
        <CurrencyConverter currencies={currencies} />
      }
    </div>
  );
}

export default App;
