import React, { useEffect, useState } from 'react';
import { Header } from "./Header/Header";
import './App.css';
import { CurrencyConverter } from './CurrencyConverter/CurrencyConverter';

// const BASE_URL = 'https://api.exchangerate.host/latest?base=UAH&symbols=USD,EUR,UAH';
const BASE_URL = 'https://api.privatbank.ua/p24api/pubinfo?json&exchange&coursid=5';

export interface ICurrency {
  base_ccy: string;
  buy: string;
  ccy: string;
  sale: string;
}

function App() {
  const [fromCurrency, setFromCurrency] = useState();
  const [toCurrency, setToCurrency] = useState();
  const [currencies, setCurrencies] = useState<ICurrency[]>([]);
  const currencyOptions = ['USD', 'EUR', 'UAH'];

  useEffect(() => {
    fetch(BASE_URL)
      .then(res => res.json())
      .then(data => setCurrencies(data))
  }, []);

  return (
    <div>
      <Header currencies={currencies} currencyOptions={currencyOptions} />
      <h1 className="title">Currency Converter</h1>
      <CurrencyConverter />
    </div>
  );
}

export default App;
