import { createUseStyles } from 'react-jss';
import { relativeValue } from '../styleUtils';
import { COLOR } from '../styleVariables';

const boxWrapper = {
  paddingLeft: relativeValue(20),
  paddingRight: relativeValue(20),
  width: relativeValue(200)
};

export const useStyles = createUseStyles({
  header: {
    width: '100vw',
    height: relativeValue(80),
    backgroundColor: COLOR.ONAHAU,
    borderRadius: '10px',
    display: 'flex',
    alignItems: 'center'
  },
  logoWrapper: {
    extend: boxWrapper,
    borderWidth: relativeValue(6),
    borderStyle: 'solid',
    borderImage: 'linear-gradient(to left, turquoise, greenyellow) 1 0',
  },
  rateWrapper: {
    extend: boxWrapper
  }
})
