import React from 'react';
import { ICurrency } from '../App';
import { useStyles } from './CurrencyConverter.styles';

interface Props {
  currencies?: ICurrency[];
  currencyOptions: string[];
}

export const CurrencyConverter:React.FC<Props> = ({ currencies, currencyOptions }) => {
  const styles = useStyles();

  return (
    <div className={styles.wrapper}>
      <div className={styles.giveWrapper}>
        {currencies && (
          <div>
            You give
            <input type="number" />
            <select name="currency">
              {currencies.map(currency => currencyOptions.includes(currency.ccy) && (
                <option value={currency.ccy}>{currency.ccy}</option>
              ))}
            </select>
          </div>
        )}
      </div>
      <div className={styles.getWrapper}>
        You get
      </div>
    </div>
  );
};
