import { createUseStyles } from 'react-jss';
import { relativeValue } from '../styleUtils';

const boxWrapper = {
  marginLeft: relativeValue(30),
  marginRight: relativeValue(30)
};

export const useStyles = createUseStyles({
  header: {
    width: '100vw',
    height: relativeValue(80),
    background: 'linear-gradient(321deg, rgba(144,214,210,1) 0%, rgba(255,255,255,1) 100%)',
    display: 'flex',
    alignItems: 'center'
  },
  logoWrapper: {
    extend: boxWrapper,
    height: relativeValue(40),
    width: relativeValue(200),
    fontSize: relativeValue(18),
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: relativeValue(6),
    borderStyle: 'solid',
    borderImage: 'linear-gradient(to left, turquoise, greenyellow) 1 0',
  },
  ratesWrapper: {
    extend: boxWrapper,
    fontSize: relativeValue(14),
  },
  rateWrapper: {
    width: relativeValue(160),
    height: relativeValue(20),
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    fontWeight: '700'
  },
  rateValue: {
    fontWeight: '500'
  }
})
