import React from 'react';
import { useStyles } from './Header.styles';
import { ICurrency } from '../App';

interface Props {
  currencies?: ICurrency[];
  currencyOptions: string[];
}

export const Header:React.FC<Props> = ({ currencies, currencyOptions }) => {
  const styles = useStyles();
  return (
    <header className={styles.header}>
      <div className={styles.logoWrapper}>Currency Convector</div>
      {currencies && (
        <div className={styles.ratesWrapper}>
          {currencies.map((currency) => currencyOptions.includes(currency.ccy) && (
            <div className={styles.rateWrapper} key={currency.ccy}>
              <p>{currency.ccy}</p>
              <p className={styles.rateValue} title='Buy'>{Number(currency.buy).toFixed(2)}</p>
              <p className={styles.rateValue} title='Buying'>{Number(currency.sale).toFixed(2)}</p>
            </div>
          ))}
        </div>
      )}
    </header>
  );
};
