import React, { useState } from 'react';
import { ICurrency } from '../App';
import { useStyles } from './CurrencyConverter.styles';

interface Props {
  currencies: ICurrency[];
}

export const CurrencyConverter:React.FC<Props> = ({ currencies }) => {
  const styles = useStyles();
  const [fromCurrency, setFromCurrency] = useState('UAH');
  const [toCurrency, setToCurrency] = useState('UAH');
  console.log('currencies - ', currencies);
  // const [exchangeRate, setExchangeRate] = useState(currencies[]);
  // const [amount, setAmount] = useState(1);
  // const [amountInFromCurrency, setAmountInFromCurrency] = useState(true);

  return (
    <div className={styles.wrapper}>
      <div className={styles.giveWrapper}>
        <label>
          You give
          <input type="number" />
        </label>
        <select
          name="currency"
          value={fromCurrency}
          onChange={(e) => setFromCurrency(e.target.value)}
        >
          {currencies.map(currency => (
            <option key={currency.ccy} value={currency.ccy}>{currency.ccy}</option>
          ))}
          <option value="UAH">UAH</option>
        </select>
      </div>
      <div className={styles.getWrapper}>
        <label>
          You get
          <input type="number" />
        </label>
        <select
          name="currency"
          value={toCurrency}
          onChange={(e) => setToCurrency(e.target.value)}
        >
          {currencies.map(currency => (
            <option key={currency.ccy} value={currency.ccy}>{currency.ccy}</option>
          ))}
          <option value="UAH">UAH</option>
        </select>
      </div>
    </div>
  );
};
