import { createUseStyles } from 'react-jss';

export const useStyles = createUseStyles({
  wrapper: {
    borderRadius: '50%',
    width: '800px',
    minHeight: '200px',
    margin: '0 auto',
    marginTop: '30px',
    display: 'flex',
  },
  input: {
    marginLeft: '10px',
  },
  label: {
    display: 'flex',
    flexDirection: 'raw'
  },
  giveWrapper: {
    width: '50%',
    backgroundColor: '#fff',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  getWrapper: {
    width: '50%',
    backgroundColor: '#b0d8dd',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  }
});
